import styled from "styled-components";
//모달창 뒷부분을 어둡게 만들어주는 컴포넌트이다.
export const BaseModal = styled.div`
  background-color: rgba(0, 0, 0, 0.4);
  width: 100%;
  height: 100vh;
  z-index: 10;
  position: fixed;
  top: 0;
  left: 0;
`;

// 모달창(화면 중앙, 최상단으로 노출)
export const BaseModalPage = styled.div`
  width: 360px;
  height: 330px;
  z-index: 100;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  border-radius: 10px;
  box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.25);
`;
